import { NextResponse } from 'next/server';
import { query } from '@/lib/db';
import { getAuthUser } from '@/lib/auth';
import { logExport } from '@/lib/audit';
import puppeteer from 'puppeteer';
// Logo Base64 strings embedded directly
const ISKI_LOGO = 'data:image/png;base64,';
const IBB_LOGO = 'data:image/png;base64,';

function applyScale(value, scale) {
  if (!scale) return value;
  let v = value;
  if (scale.multiply_factor && scale.multiply_factor !== 1) v *= scale.multiply_factor;
  if (scale.divide_factor && scale.divide_factor !== 1 && scale.divide_factor !== 0) v /= scale.divide_factor;
  if (scale.offset_value) v += scale.offset_value;
  if (scale.decimal_precision !== null && scale.decimal_precision !== undefined) {
    v = parseFloat(v.toFixed(scale.decimal_precision));
  }
  return v;
}

export async function POST(request) {
  try {
    const user = await getAuthUser();
    if (!user) return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });

    const body = await request.json();
    const { tag_ids, start_date, end_date, min_value, max_value, report_name = 'Rapor', orientation = 'portrait', scope = 'full', columns = [], rows: frontendRows = [], colorAlerts = {}, headerFontSize = 13, action } = body;

    let resultRows = [];
    let isFiltered = scope === 'filtered';

    if (isFiltered) {
      resultRows = frontendRows;
    } else {
      if (!tag_ids || tag_ids.length === 0) {
        return NextResponse.json({ error: 'tag_ids gereklidir' }, { status: 400 });
      }

      // Get settings
      const settingsResult = await query("SELECT key, value FROM rc_settings WHERE key IN ('company_name')");
      const settings = {};
      settingsResult.rows.forEach(r => { settings[r.key] = r.value; });

      // Get measurements
      let conditions = ['m.tag_id = ANY($1)'];
      let params = [tag_ids];
      let paramIndex = 2;
      if (start_date) { conditions.push(`m.timestamp >= $${paramIndex}`); params.push(start_date); paramIndex++; }
      if (end_date) { conditions.push(`m.timestamp <= $${paramIndex}`); params.push(end_date); paramIndex++; }
      if (min_value !== undefined && min_value !== null) { conditions.push(`m.value >= $${paramIndex}`); params.push(parseFloat(min_value)); paramIndex++; }
      if (max_value !== undefined && max_value !== null) { conditions.push(`m.value <= $${paramIndex}`); params.push(parseFloat(max_value)); paramIndex++; }

      const result = await query(
        `SELECT m.timestamp, m.value, t.name as tag_name, t.unit as tag_unit,
                ts.multiply_factor, ts.divide_factor, ts.offset_value, ts.decimal_precision, ts.unit as scale_unit
         FROM measurements m
         JOIN tags t ON m.tag_id = t.id
         LEFT JOIN rc_tag_scales ts ON t.id = ts.tag_id
         WHERE ${conditions.join(' AND ')}
         ORDER BY m.timestamp DESC
         LIMIT 50000`,
        params
      );
      resultRows = result.rows;
    }

    const isLandscape = orientation === 'landscape';
    const pageSize = isLandscape ? '@page { size: A4 landscape; margin: 15mm; }' : '@page { size: A4 portrait; margin: 15mm; }';
    const now = new Date().toLocaleString('tr-TR');

    let tableHeadHTML = '';
    let tableBodyHTML = '';

    if (isFiltered) {
      // Built exact table as seen on screen
      tableHeadHTML = `<tr>
            <th style="font-size: ${headerFontSize}px; text-align: left; border: 1px solid #cbd5e1; padding: 6px 10px; background: #e2e8f0;">Tarih/Saat</th>
            ${columns.map(col => `<th style="font-size: ${headerFontSize}px; text-align: left; border: 1px solid #cbd5e1; padding: 6px 10px; background: #e2e8f0;">${col}</th>`).join('')}
        </tr>`;

      tableBodyHTML = resultRows.map(row => {
        let rowHtml = `<tr><td style="font-weight: bold; text-align: left; border: 1px solid #e2e8f0; padding: 4px 10px;">${row.timestamp}</td>`;
        columns.forEach(col => {
          let valStr = row[col] !== undefined ? row[col] : '-';
          let rawVal = row[col + '_raw'];
          let alertColor = '';

          const alerts = colorAlerts[col];
          if (rawVal !== undefined && alerts) {
            let currentVal = Number(rawVal);
            if (typeof valStr === 'string' && valStr !== '-') {
              let numStr = valStr.split(' ')[0];
              if (numStr.includes('.') && numStr.includes(',')) numStr = numStr.replace(/\\./g, '').replace(',', '.');
              else if (numStr.includes(',')) numStr = numStr.replace(',', '.');
              const parsed = parseFloat(numStr);
              if (!isNaN(parsed)) currentVal = parsed;
            }
            const maxLimit = (alerts.max !== undefined && alerts.max !== '') ? Number(alerts.max.toString().replace(',', '.')) : NaN;
            const minLimit = (alerts.min !== undefined && alerts.min !== '') ? Number(alerts.min.toString().replace(',', '.')) : NaN;

            if (!isNaN(maxLimit) && currentVal > maxLimit) alertColor = alerts.maxColor || '#ef4444';
            else if (!isNaN(minLimit) && currentVal < minLimit) alertColor = alerts.minColor || '#3b82f6';
          }

          let style = alertColor ? `color: ${alertColor}; font-weight: bold;` : '';
          rowHtml += `<td style="text-align: left; border: 1px solid #e2e8f0; padding: 4px 10px; ${style}">${valStr}</td>`;
        });
        rowHtml += `</tr>`;
        return rowHtml;
      }).join('');
    } else {
      // Build raw table
      tableHeadHTML = `<tr><th style="text-align: left; border: 1px solid #cbd5e1; padding: 6px 10px; background: #e2e8f0;">Tarih/Saat</th><th style="text-align: left; border: 1px solid #cbd5e1; padding: 6px 10px; background: #e2e8f0;">Tag Ad</th><th style="text-align: left; border: 1px solid #cbd5e1; padding: 6px 10px; background: #e2e8f0;">Ham Deer</th><th style="text-align: left; border: 1px solid #cbd5e1; padding: 6px 10px; background: #e2e8f0;">lenmi Deer</th><th style="text-align: left; border: 1px solid #cbd5e1; padding: 6px 10px; background: #e2e8f0;">Birim</th></tr>`;
      tableBodyHTML = resultRows.map(row => {
        const scale = row.multiply_factor ? row : null;
        const scaledValue = applyScale(row.value, scale);
        const unit = row.scale_unit || row.tag_unit || '';
        return `<tr>
                <td style="text-align: left; border: 1px solid #e2e8f0; padding: 4px 10px;">${new Date(row.timestamp).toLocaleString('tr-TR')}</td>
                <td style="text-align: left; border: 1px solid #e2e8f0; padding: 4px 10px;">${row.tag_name}</td>
                <td style="text-align: left; border: 1px solid #e2e8f0; padding: 4px 10px;">${row.value}</td>
                <td style="text-align: left; border: 1px solid #e2e8f0; padding: 4px 10px; font-weight: bold;">${scaledValue}</td>
                <td style="text-align: left; border: 1px solid #e2e8f0; padding: 4px 10px;">${unit}</td>
            </tr>`;
      }).join('');
    }

    const filterInfo = [];
    if (start_date) filterInfo.push(`Balang: ${start_date}`);
    if (end_date) filterInfo.push(`Biti: ${end_date}`);
    if (min_value !== undefined) filterInfo.push(`Min: ${min_value}`);
    if (max_value !== undefined) filterInfo.push(`Max: ${max_value}`);

    // Build Limits Legend
    const legendItems = [];
    if (isFiltered && colorAlerts) {
      Object.entries(colorAlerts).forEach(([tag, alerts]) => {
        const hasMax = alerts.max !== undefined && alerts.max !== '';
        const hasMin = alerts.min !== undefined && alerts.min !== '';

        if (hasMax || hasMin) {
          let text = `<strong>${tag}:</strong> `;
          if (hasMax) text += `Deer > ${alerts.max} ise <span style="color:${alerts.maxColor || '#ef4444'}"> Renkli</span>`;
          if (hasMax && hasMin) text += ' | ';
          if (hasMin) text += `Deer < ${alerts.min} ise <span style="color:${alerts.minColor || '#3b82f6'}"> Renkli</span>`;
          legendItems.push(text);
        }
      });
    }

    const iskiLogoBase64 = ISKI_LOGO;
    const ibbLogoBase64 = IBB_LOGO;

    const html = `<!DOCTYPE html>
<html lang="tr">
<head>
<meta charset="utf-8">
<title>${report_name}</title>
<style>
  ${pageSize}
  * { margin: 0; padding: 0; box-sizing: border-box; }
  body { font-family: 'Segoe UI', Arial, sans-serif; font-size: 10px; color: #1a1a2e; padding: 15px; background-color: #ffffff; }
  table.antet-table { width: 100%; border-collapse: collapse; border-bottom: 2px solid #0b2e59; margin-bottom: 8px; padding-bottom: 5px; }
  table.antet-table td { border: none; vertical-align: middle; padding: 5px; }
  
  .details-bar { font-size: 9px; color: #334155; margin-bottom: 12px; padding: 4px 0; border-bottom: 1px solid #e2e8f0; display: block; }
  .details-item { display: inline-block; margin-right: 15px; }
  .details-item strong { color: #0f172a; }

  .filters-box { background: #f0f4ff; padding: 8px 12px; font-size: 9px; border: 1px dashed #dde; margin-bottom: 10px; }
  .legends-box { background: #fffbeb; padding: 8px 12px; font-size: 9px; border: 1px solid #fde68a; margin-bottom: 10px; border-radius: 4px; color: #92400e; }
  
  table.data-table { width: 100%; border-collapse: collapse; margin-top: 5px; }
  table.data-table th { background: #f1f5f9; color: #1e293b; padding: 6px 8px; text-align: left; font-size: 10px; border: 1px solid #cbd5e1; }
  table.data-table td { padding: 4px 8px; border: 1px solid #cbd5e1; font-size: 9px; text-align: left; }
  table.data-table tr:nth-child(even) { background: #fafafa; }
  
  .footer { margin-top: 20px; width: 100%; text-align: center; font-size: 8px; padding: 8px; color: #6b7280; border-top: 1px solid #e5e7eb; }
</style>
</head>
<body style="background-color: #ffffff;">

<table class="antet-table">
  <tr>
    <td style="width: 20%; text-align: left;">
      ${iskiLogoBase64 ? `<img src="${iskiLogoBase64}" width="70" height="70" style="object-fit: contain; max-height: 70px;" alt="SK" style="display: block;" />` : ''}
    </td>
    <td style="width: 60%; text-align: center;">
      <div style="font-size: 16px; font-weight: bold; color: #0b2e59; text-transform: uppercase;">merli Su Artma ube Mdrl</div>
    </td>
    <td style="width: 20%; text-align: right;">
      ${ibbLogoBase64 ? `<img src="${ibbLogoBase64}" width="70" height="50" style="object-fit: contain; max-height: 50px;" alt="BB" style="display: block; margin-left: auto;" />` : ''}
    </td>
  </tr>
</table>

<div class="details-bar">
  <span class="details-item"><strong>Rapor Ad:</strong> ${report_name}</span>
  <span class="details-item"><strong>Oluturan:</strong> ${user.fullname || user.username}</span>
  <span class="details-item"><strong>Tarih:</strong> ${now}</span>
  <span class="details-item"><strong>Kayt Says:</strong> ${resultRows.length}</span>
</div>

${filterInfo.length > 0 ? `<div class="filters-box"><strong>Filtreler:</strong> ${filterInfo.join(' | ')}</div>` : ''}
${legendItems.length > 0 ? `<div class="legends-box"><strong>Aktif Limit Uyarlar:</strong><br/>${legendItems.join('<br/>')}</div>` : ''}

<table class="data-table">
  <thead>
    ${tableHeadHTML}
  </thead>
  <tbody>
    ${tableBodyHTML}
  </tbody>
</table>

<div class="footer">Rapor Sonu</div>

</body>
</html>`;

    await logExport(user, 'pdf', report_name, { tag_ids, start_date, end_date, rowCount: resultRows.length, orientation, scope });

    if (action === 'download') {
      try {
        const browser = await puppeteer.launch();
        const page = await browser.newPage();
        await page.setContent(html, { waitUntil: 'networkidle0' });
        const pdfBuffer = await page.pdf({
          format: 'A4',
          landscape: orientation === 'landscape',
          printBackground: true,
          margin: { top: '15mm', right: '15mm', bottom: '15mm', left: '15mm' }
        });
        await browser.close();

        return new NextResponse(pdfBuffer, {
          status: 200,
          headers: {
            'Content-Type': 'application/pdf',
            'Content-Disposition': `attachment; filename="rapor_${new Date().getTime()}.pdf"`
          }
        });
      } catch (err) {
        console.error('Puppeteer generation error:', err);
        return NextResponse.json({ error: 'PDF oluturulamad: ' + err.message }, { status: 500 });
      }
    }

    return NextResponse.json({ html });
  } catch (error) {
    console.error('PDF export error:', error);
    return NextResponse.json({ error: 'Sunucu hatas' }, { status: 500 });
  }
}
